mapper.select:
select b.no ,b.user_no as userNo ,b.title ,b.content ,b.hit ,date_format(b.reg_date,'%Y-%m-%d') as regDate ,u.no ,u.name from users u, board b where u.no = b.user_no
and title like concat('%',#{kwd},'%')
order by b.no asc limit #{startRowNo}, #{listCnt}

mapper.update:

update board
set    title = #{title}
	  ,content = #{content}
where  user_no = #{userNo}
and    no = #{no}
